import React from "react";
import ProductItem from "./ProductItem";
import FetchButton from "./UI/button/FetchButton";
import '../styles/ProductList.css';
import axios from "axios";

const ProductList = ({products, setProducts, title}) => {

    const fetchData = async () => {
        const response = await axios.get("https://localhost:7256/api/Products/get-filtered-data");
        console.log(response.data);
        setProducts(response.data);
    };

    return (
        <div className="product-list-container">
            <h1 className="product-list-title">{title}</h1>
            <FetchButton fetchData={fetchData}/>
            <div className="product-list">
                {products.map(product => (
                <ProductItem product={product} key={product.id} />
                ))}
            </div>
        </div>
    );
};

export default ProductList;
