import React, { useState } from 'react';
import './FetchButton.css';
import { fetchProducts } from '../../../api/ProductsApi';
import FilterValue from '../filter/FilterValue';

const FetchButton = ({setProducts}) => {
  const [filters, setFilters] = useState({});

  const fetchData = async () => {
      const products = await fetchProducts();
      setProducts(products);
  };

  const handleFilterChange = (key, value) => {
    setFilters(prevFilters => ({
      ...prevFilters,
      [key]: value
    }));
    console.log(filters);
  };

  return (
    <div>
      <FilterValue label="Value" onChange={(value) => handleFilterChange('value', value)} />
      <FilterValue label="Code" onChange={(value) => handleFilterChange('code', value)} />
      <button className="fetch-button" onClick={() => fetchData(value, code)}>
        Fetch Data
      </button>
    </div>
  );
};

export default FetchButton;
